import React, { useState } from "react";
import AboutMe from "./aboutme";

export default function SideBar() {
  const [showAboutMe, setShowAboutMe] = useState(false);

  const handleAboutMeClick = () => {
    setShowAboutMe(true);
  };

  if (showAboutMe) {
    return <AboutMe />;
  }

  return (
    <div className="sidebar">
      <div className="sidebar-text">
        <button id="aboutme" onClick={handleAboutMeClick}>
          ABOUT ME
        </button>
        <a href="projects.html" className="sidebar-item" style={{ display: showAboutMe ? 'none' : 'block' }}>
          <button id="boogiebtn">PROJECTS</button>
        </a>
        <a href="contact.html" className="sidebar-item" style={{ display: showAboutMe ? 'none' : 'block' }}>
          <button id="contact">CONTACT</button>
        </a>
      </div>
    </div>
  );
}
