import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';
import { Helmet } from 'react-helmet';
import React, { useEffect } from 'react';


export default function Home(){

    return(
            <div className="Home">
              <Helmet>
                <link
                  href="https://fonts.googleapis.com/css2?family=Inter:wght@100;200;300;400;500;600;700&display=swap"
                  rel="stylesheet"
                />
                <link rel="icon" href="KA.png"></link>
              </Helmet>
              <div class="row home-page-container" >

        <div class="col-8 kaisa-arumeel-text">
            <div class="kaisa-text">
              <p><span class="drop-letter drop-k">K</span>aisa</p>
            </div>
            <div class="arumeel-text">
                <p><span class="drop-a">A</span>rumeel</p>
              </div>
          </div>

        <div class="col-4 menu-on-home-page">

            <div class="overlay">  </div>

            <div class="sidebar">
                <div class="sidebar-text">
                <a href="about.html" class="sidebar-item">
                    <button id="aboutme">ABOUT ME</button>
                </a>
                <a href="projects.html" class="sidebar-item">
                    <button id="boogiebtn">PROJECTS</button>
                </a>
                <a href="contact.html" class="sidebar-item">
                    <button id="contact">CONTACT</button>
                </a>
                </div>
            </div>
            <div class ="menu-container">
                <button class="hamburger" onclick="hideButton()">
                    <div class="bar"></div>
                    <div class="bar"></div>
                <div class="bar"></div>
                </button>
            </div> 
        </div>

        </div>
            </div>

    );
}

useEffect(() => {
    const hamburger = document.getElementsByClassName('hamburger')[0];
    const sidebar = document.getElementsByClassName('sidebar')[0];
    const overlay = document.getElementsByClassName('overlay')[0];
    const aboutbtn = document.getElementsByClassName('aboutme')[0];

    let menuOpen = false;

    function openMenu() {
      menuOpen = true;
      overlay.style.display = 'block';
      sidebar.style.width = '45vh';
    }

    function closeMenu() {
      menuOpen = false;
      overlay.style.display = 'none';
      sidebar.style.width = '0px';
    }

    hamburger.addEventListener('click', function() {
      if (!menuOpen) {
        openMenu();
        hamburger.style.display = "none";
      }
    });

    overlay.addEventListener('click', function() {
      if (menuOpen) {
        closeMenu();
        hamburger.style.display = "block";
      }
    });
  }, []);
